import{_ as i,c as e,o as l,a3 as r}from"./chunks/framework.KoSEt7m1.js";const f=JSON.parse('{"title":"AI 使用规范与技巧","description":"","frontmatter":{},"headers":[],"relativePath":"ai-guidelines/index.md","filePath":"ai-guidelines/index.md"}'),t={name:"ai-guidelines/index.md"};function o(n,a,h,d,s,u){return l(),e("div",null,[...a[0]||(a[0]=[r('<h1 id="ai-使用规范与技巧" tabindex="-1">AI 使用规范与技巧 <a class="header-anchor" href="#ai-使用规范与技巧" aria-label="Permalink to &quot;AI 使用规范与技巧&quot;">​</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p>本文档旨在为使用通义灵码助手的企业版用户提供详细的使用规范和技巧，帮助前端开发人员提高开发效率和代码质量，同时确保AI使用的安全性。</p><h2 id="目录" tabindex="-1">目录 <a class="header-anchor" href="#目录" aria-label="Permalink to &quot;目录&quot;">​</a></h2><ul><li><a href="/fe-workspace/ai-guidelines/basic-usage">基础使用技巧</a></li><li><a href="/fe-workspace/ai-guidelines/advanced-techniques">高阶使用技巧</a></li><li><a href="/fe-workspace/ai-guidelines/prompt-engineering">提示词与指令规范</a></li><li><a href="/fe-workspace/ai-guidelines/code-quality">代码质量保障</a></li><li><a href="/fe-workspace/ai-guidelines/security-guidelines">安全使用规范</a></li><li><a href="/fe-workspace/ai-guidelines/team-collaboration">团队协作最佳实践</a></li><li><a href="/fe-workspace/ai-guidelines/project-integration">项目集成指南</a></li></ul><h2 id="适用范围" tabindex="-1">适用范围 <a class="header-anchor" href="#适用范围" aria-label="Permalink to &quot;适用范围&quot;">​</a></h2><p>本规范适用于公司内所有使用通义灵码助手企业版的前端开发人员，包括但不限于：</p><ul><li>前端工程师</li><li>全栈工程师</li><li>技术负责人</li><li>代码审查人员</li></ul><h2 id="核心原则" tabindex="-1">核心原则 <a class="header-anchor" href="#核心原则" aria-label="Permalink to &quot;核心原则&quot;">​</a></h2><h3 id="_1-效率提升原则" tabindex="-1">1. 效率提升原则 <a class="header-anchor" href="#_1-效率提升原则" aria-label="Permalink to &quot;1. 效率提升原则&quot;">​</a></h3><ul><li>利用AI辅助减少重复性编码工作</li><li>加速问题排查和解决方案寻找</li><li>提高代码生成和文档编写的效率</li></ul><h3 id="_2-质量保障原则" tabindex="-1">2. 质量保障原则 <a class="header-anchor" href="#_2-质量保障原则" aria-label="Permalink to &quot;2. 质量保障原则&quot;">​</a></h3><ul><li>生成的代码必须经过人工审查</li><li>遵循团队既定的编码规范</li><li>确保代码的可维护性和可读性</li></ul><h3 id="_3-安全第一原则" tabindex="-1">3. 安全第一原则 <a class="header-anchor" href="#_3-安全第一原则" aria-label="Permalink to &quot;3. 安全第一原则&quot;">​</a></h3><ul><li>严禁在AI工具中输入敏感信息</li><li>避免泄露公司内部业务逻辑</li><li>定期审查AI生成的代码安全性</li></ul><h2 id="使用前提" tabindex="-1">使用前提 <a class="header-anchor" href="#使用前提" aria-label="Permalink to &quot;使用前提&quot;">​</a></h2><h3 id="技术环境" tabindex="-1">技术环境 <a class="header-anchor" href="#技术环境" aria-label="Permalink to &quot;技术环境&quot;">​</a></h3><ul><li>已安装并配置通义灵码助手企业版</li><li>VS Code 或支持的 JetBrains IDE</li><li>稳定的网络连接</li></ul><h3 id="知识准备" tabindex="-1">知识准备 <a class="header-anchor" href="#知识准备" aria-label="Permalink to &quot;知识准备&quot;">​</a></h3><ul><li>熟悉团队编码规范</li><li>了解项目架构和代码结构</li><li>掌握基本的AI工具操作技能</li></ul><h2 id="责任与义务" tabindex="-1">责任与义务 <a class="header-anchor" href="#责任与义务" aria-label="Permalink to &quot;责任与义务&quot;">​</a></h2><h3 id="开发人员责任" tabindex="-1">开发人员责任 <a class="header-anchor" href="#开发人员责任" aria-label="Permalink to &quot;开发人员责任&quot;">​</a></h3><ol><li>正确使用AI工具提升工作效率</li><li>对AI生成的代码进行严格审查</li><li>遵守安全使用规范</li><li>及时反馈使用中的问题和建议</li></ol><h3 id="技术负责人责任" tabindex="-1">技术负责人责任 <a class="header-anchor" href="#技术负责人责任" aria-label="Permalink to &quot;技术负责人责任&quot;">​</a></h3><ol><li>制定和维护AI使用规范</li><li>监督团队成员的使用情况</li><li>收集和分析使用效果数据</li><li>组织培训和经验分享活动</li></ol><hr><p><em>文档版本：v1.0</em><br><em>最后更新：2025-12-16</em></p>',27)])])}const b=i(t,[["render",o]]);export{f as __pageData,b as default};
