import{_ as a,c as i,o as e,a3 as r}from"./chunks/framework.KoSEt7m1.js";const b=JSON.parse('{"title":"设计规范","description":"","frontmatter":{},"headers":[],"relativePath":"specs/design/index.md","filePath":"specs/design/index.md"}'),t={name:"specs/design/index.md"};function o(n,l,h,s,u,d){return e(),i("div",null,[...l[0]||(l[0]=[r('<h1 id="设计规范" tabindex="-1">设计规范 <a class="header-anchor" href="#设计规范" aria-label="Permalink to &quot;设计规范&quot;">​</a></h1><p>技术设计规范定义了技术方案、架构设计的标准和最佳实践，确保技术方案的完整性和可行性。</p><h2 id="规范列表" tabindex="-1">规范列表 <a class="header-anchor" href="#规范列表" aria-label="Permalink to &quot;规范列表&quot;">​</a></h2><h3 id="技术方案设计规范" tabindex="-1"><a href="./technical-design">技术方案设计规范</a> <a class="header-anchor" href="#技术方案设计规范" aria-label="Permalink to &quot;[技术方案设计规范](./technical-design.md)&quot;">​</a></h3><p>规范技术方案的设计流程、文档结构和评审标准。</p><p><strong>核心内容：</strong></p><ul><li>技术方案设计流程</li><li>方案文档结构规范</li><li>技术选型原则</li><li>方案评审标准</li></ul><p><strong>适用场景：</strong></p><ul><li>新功能开发</li><li>架构升级改造</li><li>技术选型决策</li><li>复杂问题解决</li></ul><h3 id="技术设计文档模板" tabindex="-1"><a href="./technical-document">技术设计文档模板</a> <a class="header-anchor" href="#技术设计文档模板" aria-label="Permalink to &quot;[技术设计文档模板](./technical-document.md)&quot;">​</a></h3><p>标准化的技术设计文档模板，可直接用于项目。</p><p><strong>包含章节：</strong></p><ul><li>文档概述</li><li>需求分析</li><li>技术方案设计</li><li>技术实现细节</li><li>测试方案</li><li>部署与运维</li><li>风险评估</li></ul><h3 id="架构设计规范" tabindex="-1"><a href="./architecture">架构设计规范</a> <a class="header-anchor" href="#架构设计规范" aria-label="Permalink to &quot;[架构设计规范](./architecture.md)&quot;">​</a></h3><p>系统架构设计、微前端架构等架构层面的规范。</p><p><strong>核心内容：</strong></p><ul><li>系统架构设计原则</li><li>微前端架构规范</li><li>模块划分和通信</li><li>技术架构选型</li></ul><h2 id="技术方案设计流程" tabindex="-1">技术方案设计流程 <a class="header-anchor" href="#技术方案设计流程" aria-label="Permalink to &quot;技术方案设计流程&quot;">​</a></h2><h3 id="_1-需求分析" tabindex="-1">1. 需求分析 <a class="header-anchor" href="#_1-需求分析" aria-label="Permalink to &quot;1. 需求分析&quot;">​</a></h3><ul><li>理解业务需求</li><li>分析技术约束</li><li>识别关键问题</li><li>定义设计目标</li></ul><h3 id="_2-方案调研" tabindex="-1">2. 方案调研 <a class="header-anchor" href="#_2-方案调研" aria-label="Permalink to &quot;2. 方案调研&quot;">​</a></h3><ul><li>技术选型调研</li><li>竞品分析</li><li>可行性验证</li><li>POC 验证</li></ul><h3 id="_3-方案设计" tabindex="-1">3. 方案设计 <a class="header-anchor" href="#_3-方案设计" aria-label="Permalink to &quot;3. 方案设计&quot;">​</a></h3><ul><li>整体架构设计</li><li>模块划分</li><li>接口设计</li><li>数据流设计</li></ul><h3 id="_4-方案评审" tabindex="-1">4. 方案评审 <a class="header-anchor" href="#_4-方案评审" aria-label="Permalink to &quot;4. 方案评审&quot;">​</a></h3><ul><li>提交设计文档</li><li>召开评审会议</li><li>收集反馈意见</li><li>优化设计方案</li></ul><h3 id="_5-方案实施" tabindex="-1">5. 方案实施 <a class="header-anchor" href="#_5-方案实施" aria-label="Permalink to &quot;5. 方案实施&quot;">​</a></h3><ul><li>制定开发计划</li><li>任务分解</li><li>开发实施</li><li>持续优化</li></ul><h2 id="设计原则" tabindex="-1">设计原则 <a class="header-anchor" href="#设计原则" aria-label="Permalink to &quot;设计原则&quot;">​</a></h2><h3 id="简单性原则" tabindex="-1">简单性原则 <a class="header-anchor" href="#简单性原则" aria-label="Permalink to &quot;简单性原则&quot;">​</a></h3><ul><li>优先选择简单直接的方案</li><li>避免过度设计</li><li>保持代码可读性</li></ul><h3 id="可扩展性原则" tabindex="-1">可扩展性原则 <a class="header-anchor" href="#可扩展性原则" aria-label="Permalink to &quot;可扩展性原则&quot;">​</a></h3><ul><li>考虑未来的扩展需求</li><li>设计合理的抽象层次</li><li>遵循开闭原则</li></ul><h3 id="可维护性原则" tabindex="-1">可维护性原则 <a class="header-anchor" href="#可维护性原则" aria-label="Permalink to &quot;可维护性原则&quot;">​</a></h3><ul><li>代码结构清晰</li><li>文档完善</li><li>易于调试和修改</li></ul><h3 id="性能原则" tabindex="-1">性能原则 <a class="header-anchor" href="#性能原则" aria-label="Permalink to &quot;性能原则&quot;">​</a></h3><ul><li>关注关键路径性能</li><li>合理使用缓存</li><li>避免不必要的计算</li></ul><h3 id="安全性原则" tabindex="-1">安全性原则 <a class="header-anchor" href="#安全性原则" aria-label="Permalink to &quot;安全性原则&quot;">​</a></h3><ul><li>数据校验和过滤</li><li>权限控制</li><li>防止常见安全漏洞</li></ul><h2 id="技术方案评审清单" tabindex="-1">技术方案评审清单 <a class="header-anchor" href="#技术方案评审清单" aria-label="Permalink to &quot;技术方案评审清单&quot;">​</a></h2><h3 id="需求分析" tabindex="-1">需求分析 <a class="header-anchor" href="#需求分析" aria-label="Permalink to &quot;需求分析&quot;">​</a></h3><ul><li>[ ] 需求理解是否准确</li><li>[ ] 边界条件是否考虑</li><li>[ ] 非功能性需求是否明确</li></ul><h3 id="技术选型" tabindex="-1">技术选型 <a class="header-anchor" href="#技术选型" aria-label="Permalink to &quot;技术选型&quot;">​</a></h3><ul><li>[ ] 技术选型是否合理</li><li>[ ] 是否考虑团队技术栈</li><li>[ ] 是否评估技术风险</li></ul><h3 id="架构设计" tabindex="-1">架构设计 <a class="header-anchor" href="#架构设计" aria-label="Permalink to &quot;架构设计&quot;">​</a></h3><ul><li>[ ] 架构是否清晰</li><li>[ ] 模块划分是否合理</li><li>[ ] 接口设计是否完善</li></ul><h3 id="实施方案" tabindex="-1">实施方案 <a class="header-anchor" href="#实施方案" aria-label="Permalink to &quot;实施方案&quot;">​</a></h3><ul><li>[ ] 实施步骤是否明确</li><li>[ ] 时间评估是否合理</li><li>[ ] 风险是否识别和应对</li></ul><h3 id="文档质量" tabindex="-1">文档质量 <a class="header-anchor" href="#文档质量" aria-label="Permalink to &quot;文档质量&quot;">​</a></h3><ul><li>[ ] 文档结构是否完整</li><li>[ ] 描述是否清晰准确</li><li>[ ] 图表是否辅助理解</li></ul><h2 id="常用设计工具" tabindex="-1">常用设计工具 <a class="header-anchor" href="#常用设计工具" aria-label="Permalink to &quot;常用设计工具&quot;">​</a></h2><h3 id="架构图工具" tabindex="-1">架构图工具 <a class="header-anchor" href="#架构图工具" aria-label="Permalink to &quot;架构图工具&quot;">​</a></h3><ul><li><strong>Draw.io</strong> - 免费的在线绘图工具</li><li><strong>ProcessOn</strong> - 国内在线绘图工具</li><li><strong>Excalidraw</strong> - 手绘风格的图表工具</li></ul><h3 id="uml-工具" tabindex="-1">UML 工具 <a class="header-anchor" href="#uml-工具" aria-label="Permalink to &quot;UML 工具&quot;">​</a></h3><ul><li><strong>PlantUML</strong> - 文本描述的 UML 工具</li><li><strong>StarUML</strong> - 专业的 UML 建模工具</li></ul><h3 id="思维导图" tabindex="-1">思维导图 <a class="header-anchor" href="#思维导图" aria-label="Permalink to &quot;思维导图&quot;">​</a></h3><ul><li><strong>XMind</strong> - 功能强大的思维导图工具</li><li><strong>MindNode</strong> - Mac 平台的思维导图工具</li></ul><h3 id="原型设计" tabindex="-1">原型设计 <a class="header-anchor" href="#原型设计" aria-label="Permalink to &quot;原型设计&quot;">​</a></h3><ul><li><strong>Figma</strong> - 在线协作设计工具</li><li><strong>Axure</strong> - 专业的原型设计工具</li><li><strong>墨刀</strong> - 国内的原型设计工具</li></ul><h2 id="文档模板使用指南" tabindex="-1">文档模板使用指南 <a class="header-anchor" href="#文档模板使用指南" aria-label="Permalink to &quot;文档模板使用指南&quot;">​</a></h2><h3 id="选择合适的模板" tabindex="-1">选择合适的模板 <a class="header-anchor" href="#选择合适的模板" aria-label="Permalink to &quot;选择合适的模板&quot;">​</a></h3><p><strong>小型功能开发</strong></p><ul><li>使用简化版模板</li><li>重点关注核心逻辑</li><li>文档 2-3 页即可</li></ul><p><strong>中型项目开发</strong></p><ul><li>使用标准模板</li><li>完整的需求和设计</li><li>文档 5-10 页</li></ul><p><strong>大型架构升级</strong></p><ul><li>使用完整模板</li><li>详细的调研和设计</li><li>文档 10+ 页</li></ul><h3 id="填写技巧" tabindex="-1">填写技巧 <a class="header-anchor" href="#填写技巧" aria-label="Permalink to &quot;填写技巧&quot;">​</a></h3><ol><li><strong>使用图表</strong> - 一图胜千言，用流程图、架构图等辅助说明</li><li><strong>举例说明</strong> - 用具体例子说明抽象概念</li><li><strong>分层描述</strong> - 从宏观到微观，逐层深入</li><li><strong>突出重点</strong> - 用加粗、高亮等方式突出关键信息</li></ol><h2 id="相关规范" tabindex="-1">相关规范 <a class="header-anchor" href="#相关规范" aria-label="Permalink to &quot;相关规范&quot;">​</a></h2><ul><li><a href="./../coding/">代码编写规范</a> - 命名、注释、代码风格</li><li><a href="./../engineering/">工程化规范</a> - Git工作流、代码审查、测试</li><li><a href="./../optimization/">优化规范</a> - 性能优化、内存管理</li></ul>',71)])])}const q=a(t,[["render",o]]);export{b as __pageData,q as default};
